module Day19 where

import qualified Data.Map.Strict as Map
import System.IO.Unsafe

data Dir = U | D | L | R | Done
  deriving Show

type Grid = Map.Map Pos Char
type Pos = (Int,Int)

day19_1 :: String -> String
day19_1 s = fst $ walks (startPos g) D [] g 0
  where g = processInput s

day19_2 :: String -> Int
day19_2 s = snd $ walks (startPos g) D [] g 0
  where g = processInput s

startPos :: Grid -> Pos
startPos g = head $ Map.keys g

walks :: Pos -> Dir -> [Char] -> Grid -> Int -> ([Char], Int)
walks p d cs g i = case walk p d cs g of (_, Done, _)  -> (reverse cs, i)
                                         (p', d', cs') -> walks p' d' cs' g (i+1)

walk :: Pos -> Dir -> [Char] -> Grid -> (Pos,Dir,[Char])
walk p d cs g
  | g!p == '|' || g!p == '-' = (move p d, d, cs)
  | g!p == '+' = (move p d', d', cs)
  | g!p == ' ' = (p, Done, cs)
  | otherwise = (move p d, d, (g!p):cs) 
  where d' = turn g p d

(!) :: Grid -> Pos -> Char
(!) g p = case (Map.!?) g p of Just c  -> c
                               Nothing -> ' '

move :: Pos -> Dir -> Pos
move p d = case d of U -> up p
                     D -> down p
                     L -> left p
                     R -> right p

turn :: Grid -> Pos -> Dir -> Dir
turn g p d = case d of U -> snd $ head $ filter (\(p,_) -> g!p /= ' ') [(up p, U), (left p, L), (right p, R)]
                       D -> snd $ head $ filter (\(p,_) -> g!p /= ' ') [(down p, D), (left p, L), (right p, R)]
                       L -> snd $ head $ filter (\(p,_) -> g!p /= ' ') [(down p, D), (up p, U), (left p, L)]
                       R -> snd $ head $ filter (\(p,_) -> g!p /= ' ') [(down p, D), (right p, R), (up p, U)]

up :: Pos -> Pos
up (x,y) = (x-1,y)

down :: Pos -> Pos
down (x,y) = (x+1,y)

left :: Pos -> Pos
left (x,y) = (x,y-1)

right :: Pos -> Pos
right (x,y) = (x,y+1)

processInput :: String -> Grid
processInput s = Map.fromList [((x,y),(ss!!x)!!y) | x <- [0..(l-1)], y <- [0..(l'-1)], (ss!!x)!!y /= ' ']
  where ss = lines s
        l = length ss
        l' = length (head ss)


