module Day8 where

import qualified Data.Map.Strict as Map

data Instruction = Ins String Op Int String Cp Int
  deriving Show
data Op = Inc | Dec 
  deriving Show
data Cp = SLT | SGT | LEQ | GEQ | SEQ | NEQ
  deriving Show

type Registers = Map.Map String Int

processInput :: String -> [Instruction]
processInput s = map makeInstruction (map words (lines s))

makeInstruction :: [String] -> Instruction
makeInstruction (a:op:n:_:r:c:b:_) = Ins a op' (read n) r f' (read b)
  where op' = if op == "inc" then Inc else Dec
        f'  = makeComparison c 

makeComparison :: String -> Cp
makeComparison s
  | s == "<"  = SLT
  | s == ">"  = SGT
  | s == "<=" = LEQ
  | s == ">=" = GEQ
  | s == "==" = SEQ
  | s == "!=" = NEQ

day8_1 :: String -> Int
day8_1 s = maximum $ Map.elems (computeInstructions (processInput s) Map.empty)

day8_2 :: String -> Maybe Int
day8_2 s = Map.lookup "MAXIMUMVALUE" (computeInstructions (processInput s) Map.empty)

computeInstructions :: [Instruction] -> Registers -> Registers
computeInstructions [] rs = rs
computeInstructions (i:is) rs = computeInstructions is (computeInstruction i rs)

computeInstruction :: Instruction -> Registers -> Registers
computeInstruction (Ins a op n b cp m) rs = if cp' then setRegister a (processOp op v n) rs'' else rs'
  where (cp', rs') = myCompare b cp m rs
        (v, rs'')  = getRegister a rs'

processOp :: Op -> Int -> Int -> Int
processOp Inc a b = a + b
processOp Dec a b = a - b

myCompare :: String -> Cp -> Int -> Registers -> (Bool, Registers)
myCompare s cp n rs = (cp' v n, rs')
  where (v, rs') = getRegister s rs
        cp' = getComparison cp

getRegister :: String -> Registers -> (Int, Registers)
getRegister s rs = case res of Nothing -> (0, Map.insert s 0 rs)
                               Just v  -> (v, rs)
  where res = Map.lookup s rs

setRegister :: String -> Int -> Registers -> Registers
setRegister a v rs = if v > maximum (Map.elems rs) then (Map.insert "MAXIMUMVALUE" v rs') else rs'
  where rs' = Map.insert a v rs

getComparison :: Cp -> (Int -> Int -> Bool)
getComparison SLT = (<)
getComparison SGT = (>)
getComparison LEQ = (<=)
getComparison GEQ = (>=)
getComparison SEQ = (==)
getComparison NEQ = (/=)


